mapper.select:
SELECT

FROM sys_office a

WHERE a.id = #{id}

SELECT

FROM sys_office a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	AND (a.parent_id = #{parent.id})


${sqlMap.dsf}
