mapper.select:
SELECT 

FROM sys_role a
LEFT JOIN sys_office o ON o.id = a.office_id

WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND a.useable=#{useable}

	AND  exists (select 1 from sys_user_role ur 
		LEFT JOIN sys_user u ON u.id = ur.user_id where ur.role_id = a.id and u.id = #{user.id}
	)


	AND  exists (select 1 from sys_user_role ur 
		LEFT JOIN sys_user u ON u.id = ur.user_id where ur.role_id = a.id AND u.login_name = #{user.loginName}
	)


${sqlMap.dsf}
ORDER BY o.code, a.name

mapper.update:
UPDATE sys_role SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
